pragma solidity ^0.6.0;
//SPDX-License-Identifier: MIT

import '@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/utils/PausableUpgradeable.sol';
import '@openzeppelin/contracts/math/SafeMath.sol';
import '@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol';
import '@openzeppelin/contracts-upgradeable/token/ERC20/SafeERC20Upgradeable.sol';
import '@openzeppelin/contracts-upgradeable/token/ERC1155/ERC1155HolderUpgradeable.sol';
import './interfaces/IRandoms.sol';
import './interfaces/ICharacter.sol';
import './interfaces/ICrafting.sol';
import './CraftingItems.sol';
import './characters.sol';
import './weapons.sol';

enum ItemTypes {
    Part,
    FireCrystal,
    EarthCrystal,
    WaterCrystal,
    LightningCrystal
}

contract Crafting is
    Initializable,
    ERC1155HolderUpgradeable,
    AccessControlUpgradeable,
    PausableUpgradeable,
    ICrafting,
    ICharacter
{
    using SafeMath for uint256;
    using SafeERC20Upgradeable for IERC20Upgradeable;

    bytes32 public constant GAME_ADMIN = keccak256('GAME_ADMIN');

    Characters public characters;
    Weapons public weapons;
    IERC20Upgradeable public tagToken;
    IERC20Upgradeable public coreToken;
    IRandoms public randoms;
    CraftingItems public craftingItems;

    uint256 private basePartsReward;
    uint256 private traineeMultiplier;
    uint256 private commonTypeMultiplier;
    uint256 private uncommonTypeMultiplier;
    uint256 private rareTypeMultiplier;
    uint256 private epicTypeMultiplier;
    uint256 private legendaryTypeMultiplier;

    bytes4 private constant InterfaceId_ERC721 = 0x80ac58cd;
    bytes4 private constant InterfaceId_ERC1155 = 0xd9b67a26;

    event SetParts(address sender, uint256 parts, uint256 amount);
    event SetFireCrystals(address sender, uint256 fireCrystals, uint256 amount);
    event SetWaterCrystals(address sender, uint256 waterCrystals, uint256 amount);
    event SetEarthCrystals(address sender, uint256 earthCrystals, uint256 amount);
    event SetLightningCrystals(address sender, uint256 lightningCrystals, uint256 amount);
    event Salvage(address sender, uint256 reward, uint256 gunQuality);
    event LootItems(address sender, ItemTypes itemType);
    event LootedRandomItem(address sender, ItemTypes itemType);

    function initialize(
        IERC20Upgradeable _tagToken,
        IERC20Upgradeable _coreToken,
        Characters _characters,
        Weapons _weapons,
        IRandoms _randoms,
        CraftingItems _craftingItems
    ) public initializer {
        __AccessControl_init();
        __ERC1155Holder_init();
        __Pausable_init();

        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _setupRole(GAME_ADMIN, msg.sender);

        tagToken = _tagToken;
        coreToken = _coreToken;
        characters = _characters;
        weapons = _weapons;
        randoms = _randoms;
        craftingItems = _craftingItems;

        basePartsReward = 2500;
        traineeMultiplier = 2;
        commonTypeMultiplier = 1;
        uncommonTypeMultiplier = 2;
        rareTypeMultiplier = 10;
        epicTypeMultiplier = 50;
        legendaryTypeMultiplier = 200;
    }

    function setRegisterInterfaces() public {
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), 'Admin role needed');

        _registerInterface(InterfaceId_ERC721);
        _registerInterface(InterfaceId_ERC1155);
    }

    function bulkSalvage(uint256[] memory weaponsList, address user) public override whenNotPaused {
        for (uint256 i = 0; i < weaponsList.length; i++) {
            salvage(weaponsList[i], user);
        }
    }

    function salvage(uint256 weapon, address user) public override whenNotPaused {
        require(weapons.ownerOf(weapon) == msg.sender, 'Not the weapon owner');

        uint256 reward;
        uint256 quality = weapons.getQuality(weapon);
        uint256 typeMultiplier = commonTypeMultiplier;

        if (quality == 0) typeMultiplier = commonTypeMultiplier;
        if (quality == 1) typeMultiplier = uncommonTypeMultiplier;
        if (quality == 2) typeMultiplier = rareTypeMultiplier;
        if (quality == 3) typeMultiplier = epicTypeMultiplier;
        if (quality == 4) typeMultiplier = legendaryTypeMultiplier;

        if (doesHaveTrainee(user)) {
            // update random value with bonus
            reward = basePartsReward.mul(typeMultiplier).mul(traineeMultiplier);
        } else {
            // update parts with random drop value according to table
            reward = basePartsReward.mul(typeMultiplier);
        }
        addParts(reward, user);
        // burn weapon
        weapons.safeTransferFrom(msg.sender, address(0x1), weapon);
        emit Salvage(msg.sender, reward, quality);
    }

    function lootItems(
        uint256 character,
        uint256 amount,
        address user
    ) external override restricted {
        require(amount > 0, 'amount > 0 needed');
        require(characters.ownerOf(character) == user, 'Not the character owner');

        uint256 baseSeed = RandomUtil.combineSeeds(
            randoms.getRandomSeed(user),
            RandomUtil.randomSeededMinMax(0, character, randoms.getRandomSeed(user))
        );
        ItemTypes randomItem = ItemTypes(RandomUtil.randomSeededMinMax(1, 4, baseSeed));
        CharacterType ctype = characters.getCharacterType(character);

        // Trainees gets bonus rewards for crafting.
        if (ctype == CharacterType.Trainee) {
            amount = amount.mul(2);
        }

        // always yield parts
        addParts(amount, user);

        if (randomItem == ItemTypes.FireCrystal) {
            addFireCrystals(amount.div(4), user);
        }
        if (randomItem == ItemTypes.LightningCrystal) {
            addLightningCrystals(amount.div(4), user);
        }
        if (randomItem == ItemTypes.WaterCrystal) {
            addWaterCrystals(amount.div(4), user);
        }
        if (randomItem == ItemTypes.EarthCrystal) {
            addEarthCrystals(amount.div(4), user);
        }

        emit LootedRandomItem(user, randomItem);
    }

    function addParts(uint256 amount, address user) private {
        craftingItems.mintParts(user, amount);
        emit SetParts(user, craftingItems.balanceOf(user, craftingItems.PART()), amount);
    }

    function addFireCrystals(uint256 amount, address user) private {
        craftingItems.mintFireCrystals(user, amount);
        emit SetFireCrystals(user, craftingItems.balanceOf(user, craftingItems.FIRE_CRYSTAL()), amount);
    }

    function addWaterCrystals(uint256 amount, address user) private {
        craftingItems.mintWaterCrystals(user, amount);
        emit SetWaterCrystals(user, craftingItems.balanceOf(user, craftingItems.WATER_CRYSTAL()), amount);
    }

    function addEarthCrystals(uint256 amount, address user) private {
        craftingItems.mintEarthCrystal(user, amount);
        emit SetEarthCrystals(user, craftingItems.balanceOf(user, craftingItems.EARTH_CRYSTAL()), amount);
    }

    function addLightningCrystals(uint256 amount, address user) private {
        craftingItems.mintLightningCrystals(user, amount);
        emit SetLightningCrystals(user, craftingItems.balanceOf(user, craftingItems.LIGHTNING_CRYSTAL()), amount);
    }

    function subParts(uint256 amount, address user) private {
        require(craftingItems.balanceOf(user, craftingItems.PART()).sub(amount) >= 0, 'Burn amount exceeds balance');
        craftingItems.burnParts(user, amount);
        emit SetParts(user, craftingItems.balanceOf(user, craftingItems.PART()), amount);
    }

    function subFireCrystals(uint256 amount, address user) private {
        require(
            craftingItems.balanceOf(user, craftingItems.FIRE_CRYSTAL()).sub(amount) >= 0,
            'Burn amount exceeds balance'
        );
        craftingItems.burnFireCrystals(user, amount);
        emit SetFireCrystals(user, craftingItems.balanceOf(user, craftingItems.FIRE_CRYSTAL()), amount);
    }

    function subLightningCrystals(uint256 amount, address user) private {
        require(
            craftingItems.balanceOf(user, craftingItems.LIGHTNING_CRYSTAL()).sub(amount) >= 0,
            'Burn amount exceeds balance'
        );
        craftingItems.burnLightningCrystals(user, amount);
        emit SetLightningCrystals(user, craftingItems.balanceOf(user, craftingItems.LIGHTNING_CRYSTAL()), amount);
    }

    function subWaterCrystals(uint256 amount, address user) private {
        require(
            craftingItems.balanceOf(user, craftingItems.WATER_CRYSTAL()).sub(amount) >= 0,
            'Burn amount exceeds balance'
        );
        craftingItems.burnWaterCrystals(user, amount);
        emit SetWaterCrystals(user, craftingItems.balanceOf(user, craftingItems.WATER_CRYSTAL()), amount);
    }

    function subEarthCrystals(uint256 amount, address user) private {
        require(
            craftingItems.balanceOf(user, craftingItems.EARTH_CRYSTAL()).sub(amount) >= 0,
            'Burn amount exceeds balance'
        );
        craftingItems.burnEarthCrystals(user, amount);
        emit SetEarthCrystals(user, craftingItems.balanceOf(user, craftingItems.EARTH_CRYSTAL()), amount);
    }

    function doesHaveTrainee(address user) public view returns (bool) {
        uint256[] memory tokens = new uint256[](characters.balanceOf(user));
        for (uint256 i = 0; i < tokens.length; i++) {
            tokens[i] = characters.tokenOfOwnerByIndex(user, i);
            if (characters.getCharacterType(tokens[i]) == CharacterType.Trainee) {
                return true;
            }
        }
        return false;
    }

    function getMyCharacters(address user) public view returns (uint256[] memory) {
        uint256[] memory tokens = new uint256[](characters.balanceOf(user));
        for (uint256 i = 0; i < tokens.length; i++) {
            tokens[i] = characters.tokenOfOwnerByIndex(user, i);
        }
        return tokens;
    }

    function getMyWeapons(address user) public view returns (uint256[] memory) {
        uint256[] memory tokens = new uint256[](weapons.balanceOf(user));
        for (uint256 i = 0; i < tokens.length; i++) {
            tokens[i] = weapons.tokenOfOwnerByIndex(user, i);
        }
        return tokens;
    }

    modifier restricted() {
        _restricted();
        _;
    }

    function _restricted() internal view {
        require(hasRole(GAME_ADMIN, msg.sender), 'Game admin role needed.');
    }

    function pause() external restricted {
        _pause();
    }

    function unpause() external restricted {
        _unpause();
    }
}
