//SPDX-License-Identifier: MIT
pragma solidity ^0.6.0;

import '@openzeppelin/contracts-upgradeable/proxy/Initializable.sol';
import '@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol';
import '@openzeppelin/contracts/math/SafeMath.sol';
import 'abdk-libraries-solidity/ABDKMath64x64.sol';
import '@openzeppelin/contracts-upgradeable/token/ERC1155/ERC1155Upgradeable.sol';

contract CraftingItems is Initializable, ERC1155Upgradeable, AccessControlUpgradeable {
    using ABDKMath64x64 for int128;
    using SafeMath for uint256;

    uint256 public constant PART = 0;
    uint256 public constant FIRE_CRYSTAL = 1;
    uint256 public constant LIGHTNING_CRYSTAL = 2;
    uint256 public constant WATER_CRYSTAL = 3;
    uint256 public constant EARTH_CRYSTAL = 4;

    bytes32 public constant CRAFTING_ITEMS_ADMIN = keccak256('CRAFTING_ITEMS_ADMIN');

    function initialize() public initializer {
        __ERC1155_init("");
        __AccessControl_init();

        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }

    function _beforeTokenTransfer(
        address operator,
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) internal virtual override {
        super._beforeTokenTransfer(operator, from, to, ids, amounts, data);
        require(hasRole(CRAFTING_ITEMS_ADMIN, operator), 'Crafting items admin role needed.');
    }

    function mintParts(address _address, uint256 amount) public restricted {
        _mint(_address, PART, amount, '');
    }

    function burnParts(address _address, uint256 amount) public restricted {
        _burn(_address, PART, amount);
    }

    function mintFireCrystals(address _address, uint256 amount) public restricted {
        _mint(_address, FIRE_CRYSTAL, amount, '');
    }

    function burnFireCrystals(address _address, uint256 amount) public restricted {
        _burn(_address, FIRE_CRYSTAL, amount);
    }

    function mintWaterCrystals(address _address, uint256 amount) public restricted {
        _mint(_address, WATER_CRYSTAL, amount, '');
    }

    function burnWaterCrystals(address _address, uint256 amount) public restricted {
        _burn(_address, WATER_CRYSTAL, amount);
    }

    function mintEarthCrystal(address _address, uint256 amount) public restricted {
        _mint(_address, EARTH_CRYSTAL, amount, '');
    }

    function burnEarthCrystals(address _address, uint256 amount) public restricted {
        _burn(_address, EARTH_CRYSTAL, amount);
    }

    function mintLightningCrystals(address _address, uint256 amount) public restricted {
        _mint(_address, LIGHTNING_CRYSTAL, amount, '');
    }

    function burnLightningCrystals(address _address, uint256 amount) public restricted {
        _burn(_address, LIGHTNING_CRYSTAL, amount);
    }

    function updateUri(string calldata newuri) external onlyAdmin {
        _setURI(newuri);
    }


    modifier restricted() {
        _restricted();
        _;
    }

    modifier onlyAdmin(){
        _onlyAdmin();
        _;
    }

    function _restricted() internal view {
        require(hasRole(CRAFTING_ITEMS_ADMIN, msg.sender), 'Crafting items admin role needed.');
    }

    function _onlyAdmin() internal view {
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), 'Default admin role needed.');
    }
}
