// SPDX-License-Identifier: MIT
pragma solidity ^0.6.0;
pragma experimental ABIEncoderV2;

import '@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol';
import '../characters.sol';
import '../weapons.sol';
import '../RewardsManager/interfaces/IRewardsManager.sol';

interface IGameMaster {
    struct Target {
        uint24 power;
        uint8 trait;
        uint16 fireRate;
    }

    function characters() external returns (Characters);
    function weapons() external returns (Weapons);
    function tagToken() external returns (IERC20Upgradeable);
    function rewardsManager() external returns (IRewardsManager);

    function fight(uint256 char, uint256 wep, Target calldata target, uint8 fightMultiplier) external;

    function claimTokenRewards() external;
}
