// SPDX-License-Identifier: MIT
pragma solidity ^0.6.0;
pragma experimental ABIEncoderV2;

import '@openzeppelin/contracts-upgradeable/proxy/Initializable.sol';
import '@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/utils/PausableUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/token/ERC721/IERC721Upgradeable.sol';
import '@openzeppelin/contracts-upgradeable/token/ERC721/IERC721ReceiverUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol';
import '@openzeppelin/contracts-upgradeable/math/SafeMathUpgradeable.sol';
import '../interfaces/IGameMaster.sol';
import '../RewardsManager/interfaces/IRewardsManager.sol';
import '../CraftingItems.sol';

contract NFTPoolBase is IERC721ReceiverUpgradeable, Initializable, AccessControlUpgradeable, PausableUpgradeable {
    using SafeMathUpgradeable for uint256;

    IGameMaster public gameMaster;
    IERC20Upgradeable public rewardToken;
    IERC721Upgradeable public weapons;
    IERC721Upgradeable public operators;
    mapping(IERC721Upgradeable => mapping(uint256 => address)) public depositor;
    mapping(address => uint256) public tokenRewards;

    int128 public treasuryTax;
    address public treasury;
    int128 public operatorOwnerFee;
    int128 public weaponOwnerFee;
    uint256 public minClaimAmount;
    mapping(IERC721Upgradeable => uint256) public inventoryLimit;

    IRewardsManager public rewardsManager;
    CraftingItems public craftingItems;

    bytes32 public constant GAME_CONTROLLER = keccak256('GAME_CONTROLLER');
    bytes32 public constant POOL_ADMIN = keccak256('POOL_ADMIN');

    event PlayerRewardDistributed(address player, uint256 rewardAmount);
    event OperatorOwnerRewardDistributed(address owner, uint256 rewardAmount);
    event WeaponOwnerDistributed(address owner, uint256 rewardAmount);

    struct Balances {
        uint256 rewards;
        uint256 parts;
        uint256 fireCrystals;
        uint256 lightningCrystals;
        uint256 waterCrystals;
        uint256 earthCrystals;
    }

    function __NFTPoolBase_init(IGameMaster _gameMaster, address _poolAdmin) internal initializer {
        __AccessControl_init();
        __Pausable_init();

        _setupRole(POOL_ADMIN, msg.sender);
        _setupRole(GAME_CONTROLLER, msg.sender);
        _setupRole(POOL_ADMIN, _poolAdmin);
        _setRoleAdmin(POOL_ADMIN, POOL_ADMIN);

        gameMaster = _gameMaster;
        rewardToken = gameMaster.tagToken();
        weapons = gameMaster.weapons();
        operators = gameMaster.characters();
        rewardsManager = gameMaster.rewardsManager();
    }

    function onERC721Received(
        address,
        address,
        uint256,
        bytes calldata
    ) external override returns (bytes4) {
        if (msg.sender == address(operators) || msg.sender == address(weapons)) {
            return IERC721ReceiverUpgradeable.onERC721Received.selector;
        } else {
            return 0;
        }
    }

    function deposit(IERC721Upgradeable token, uint256 tokenId) public virtual whenNotPaused tokenAllowed(token) {
        require(token.balanceOf(address(this)) < inventoryLimit[token], 'Pool size limit exceeded');

        token.safeTransferFrom(msg.sender, address(this), tokenId);
        depositor[token][tokenId] = msg.sender;
    }

    function withdraw(
        IERC721Upgradeable token,
        uint256 tokenId,
        bool claimRewards
    ) public virtual deposited(token, tokenId) {
        require(depositor[token][tokenId] == msg.sender, 'Unauthorized withdrawal attempt');
        token.safeTransferFrom(address(this), msg.sender, tokenId);
        delete depositor[token][tokenId];

        if (claimRewards) {
            claimTokenRewards();
        }
    }

    function fight(
        uint256 operatorId,
        uint256 weaponId,
        IGameMaster.Target memory target,
        uint8 fightMultiplier
    ) public virtual whenNotPaused deposited(operators, operatorId) deposited(weapons, weaponId) {

        Balances memory balanceBefore = getBalances();
        gameMaster.fight(operatorId, weaponId, target, fightMultiplier);
        Balances memory balanceAfter = getBalances();

        if (balanceAfter.rewards > balanceBefore.rewards) {
            uint256 totalReward = balanceAfter.rewards.sub(balanceBefore.rewards);

            if (address(treasury) != address(0) && treasuryTax > 0) {
                uint256 taxAmount = ABDKMath64x64.mulu(treasuryTax, totalReward);
                rewardsManager.withdraw(0, taxAmount, address(this));
                rewardToken.transfer(treasury, taxAmount);
                totalReward = totalReward.sub(taxAmount);
            }

            address operatorOwner = depositor[operators][operatorId];
            uint256 operatorReward = ABDKMath64x64.mulu(operatorOwnerFee, totalReward);
            rewardsManager.transfer(0, operatorReward, operatorOwner);
            emit OperatorOwnerRewardDistributed(operatorOwner, operatorReward);

            address weaponOwner = depositor[weapons][weaponId];
            uint256 weaponReward = ABDKMath64x64.mulu(weaponOwnerFee, totalReward);
            rewardsManager.transfer(0, weaponReward, weaponOwner);
            emit WeaponOwnerDistributed(weaponOwner, weaponReward);

            totalReward = totalReward.sub(operatorReward).sub(weaponReward);
            rewardsManager.transfer(0, totalReward, msg.sender);
            emit PlayerRewardDistributed(msg.sender, totalReward);
        }

        distributePartsOrCrystals(craftingItems.PART(), balanceBefore.parts, balanceAfter.parts, operatorId, weaponId);
        distributePartsOrCrystals(craftingItems.FIRE_CRYSTAL(), balanceBefore.fireCrystals, balanceAfter.fireCrystals, operatorId, weaponId);
        distributePartsOrCrystals(craftingItems.LIGHTNING_CRYSTAL(), balanceBefore.lightningCrystals, balanceAfter.lightningCrystals, operatorId, weaponId);
        distributePartsOrCrystals(craftingItems.WATER_CRYSTAL(), balanceBefore.waterCrystals, balanceAfter.waterCrystals, operatorId, weaponId);
        distributePartsOrCrystals(craftingItems.EARTH_CRYSTAL(), balanceBefore.earthCrystals, balanceAfter.earthCrystals, operatorId, weaponId);

        // Migrate old rewards to Rewards Manager
        uint256 _tokenRewards = tokenRewards[msg.sender];
        if (_tokenRewards > 0) {
            tokenRewards[msg.sender] = 0;
            rewardToken.approve(address(rewardsManager), _tokenRewards);
            rewardsManager.deposit(0, _tokenRewards, msg.sender);
        }
    }

    function claimTokenRewards() public virtual {
        uint256 _tokenRewards = tokenRewards[msg.sender];

        require(_tokenRewards >= minClaimAmount, 'Too few rewards to claim. Please earn more befoe claiming');
        tokenRewards[msg.sender] = 0;

        rewardToken.transfer(msg.sender, _tokenRewards);
    }

    function pause() external onlyPoolAdmin {
        _pause();
    }

    function unpause() external onlyPoolAdmin {
        _unpause();
    }

    function setTreasury(address _treasury) public onlyGameController {
        treasury = _treasury;
    }

    function setTreasuryTax(int128 _tax) public onlyGameController {
        treasuryTax = _tax;
    }

    function setInventoryLimit(IERC721Upgradeable _token, uint32 _limit) public onlyGameController {
        inventoryLimit[_token] = _limit;
    }

    function setFeeDistribution(int128 _operatorOwnerFee, int128 _weaponOwnerFee) public virtual onlyPoolAdmin {
        operatorOwnerFee = _operatorOwnerFee;
        weaponOwnerFee = _weaponOwnerFee;
    }

    function setMinClaimAmount(uint256 _minClaimAmount) public onlyPoolAdmin {
        minClaimAmount = _minClaimAmount;
    }

    function setRewardsManager(IRewardsManager _rewardsManager) public onlyGameController {
        rewardsManager = _rewardsManager;
    }

    function setCraftingItems(CraftingItems _craftingItems) public onlyGameController {
        craftingItems = _craftingItems;
    }

    function getBalances() public view returns (Balances memory) {
        (uint256 rewards, ) = rewardsManager.userInfo(0, address(this));
        uint256[] memory tokenIds = new uint[](5);
        tokenIds[0] = craftingItems.PART();
        tokenIds[1] = craftingItems.FIRE_CRYSTAL();
        tokenIds[2] = craftingItems.LIGHTNING_CRYSTAL();
        tokenIds[3] = craftingItems.WATER_CRYSTAL();
        tokenIds[4] = craftingItems.EARTH_CRYSTAL();

        address[] memory addresses = new address[](5);
        addresses[0] = address(this);
        addresses[1] = address(this);
        addresses[2] = address(this);
        addresses[3] = address(this);
        addresses[4] = address(this);

        uint256[] memory balances = craftingItems.balanceOfBatch(addresses,tokenIds);

        return Balances(rewards, balances[0], balances[1], balances[2], balances[3], balances[4]);
    }

    function distributePartsOrCrystals(uint256 tokenId, uint256 balanceBefore, uint256 balanceAfter, uint256 operatorId, uint256 weaponId) private {
        if (balanceAfter <= balanceBefore) return;

        uint256 totalReward = balanceAfter.sub(balanceBefore);

        address operatorOwner = depositor[operators][operatorId];
        uint256 operatorReward = ABDKMath64x64.mulu(operatorOwnerFee, totalReward);
        craftingItems.safeTransferFrom(address(this), operatorOwner, tokenId, operatorReward, '');

        address weaponOwner = depositor[weapons][weaponId];
        uint256 weaponReward = ABDKMath64x64.mulu(weaponOwnerFee, totalReward);
        craftingItems.safeTransferFrom(address(this), weaponOwner, tokenId, weaponReward, '');

        totalReward = totalReward.sub(operatorReward).sub(weaponReward);
        craftingItems.safeTransferFrom(address(this), msg.sender, tokenId, totalReward, '');
    }

    modifier deposited(IERC721Upgradeable token, uint256 tokenId) {
        require(token.ownerOf(tokenId) == address(this), 'The asset is not deposited in the pool');
        _;
    }

    modifier tokenAllowed(IERC721Upgradeable token) {
        require(token == weapons || token == operators, 'This type of NFT may not be deposited here');
        _;
    }

    modifier onlyPoolAdmin() {
        require(hasRole(POOL_ADMIN, msg.sender), 'Pool admin role needed');
        _;
    }

    modifier onlyGameController() {
        require(hasRole(GAME_CONTROLLER, msg.sender), 'Game controller role needed');
        _;
    }
}
