pragma solidity ^0.6.2;

import '@openzeppelin/contracts/token/ERC20/ERC20.sol';
import '@openzeppelin/contracts/access/Ownable.sol';

/**
 * @title Part
 * @dev Very simple ERC20 Token example, where all tokens are pre-assigned to the creator.
 * Note they can later distribute these tokens as they wish using `transfer` and other
 * `ERC20` functions.
 * Based on https://github.com/OpenZeppelin/openzeppelin-contracts/blob/v2.5.1/contracts/examples/SimpleToken.sol
 */
contract Part is ERC20, Ownable {
    /**
     * @dev Constructor that gives msg.sender all of existing tokens.
     */
    constructor() public ERC20('Cipher Parts', 'CPRT') {
        // _mint(address(this), 1000000000 * (10**uint256(decimals())));
        // _approve(address(this), msg.sender, totalSupply());
    }

    /**
     * @dev Method to mint Parts later on - While we do not plan to use this method, we reserve the right to do in order for the sustainability of the game.
     */
    function mint(uint256 amount) external onlyOwner {
        _mint(msg.sender, amount);
    }
}

